(function(){'use strict';function initPrivacyBanner(){var banner=document.getElementById('privacy-banner');if(!banner)return;var closeBtn=banner.querySelector('.privacy-banner__close');if(!closeBtn)return;var dismissed=localStorage.getItem('privacyBannerDismissed');if(dismissed){banner.classList.add('hidden');return;}setTimeout(function(){banner.classList.remove('hidden');},500);closeBtn.addEventListener('click',function(){banner.classList.add('hidden');localStorage.setItem('privacyBannerDismissed','true');});document.addEventListener('keydown',function(e){if(e.key==='Escape'&&!banner.classList.contains('hidden')){banner.classList.add('hidden');localStorage.setItem('privacyBannerDismissed','true');}});}if(document.readyState==='loading'){document.addEventListener('DOMContentLoaded',initPrivacyBanner);}else{initPrivacyBanner();}})();